mapper.select:
SELECT COUNT(*)
  FROM PAYMENT
 WHERE EMAIL = #{email}
   AND PASS_NO = #{passNo}
   AND EXPIRATION_DATE > SYSDATE

mapper.update:
		UPDATE PAYMENT
  		   SET EXPIRATION_DATE = EXPIRATION_DATE + 30
  		 WHERE EMAIL = #{email}

mapper.select:
		SELECT FLOOR(EXPIRATION_DATE - SYSDATE)
 		  FROM PAYMENT
	     WHERE EMAIL = #{email}

		SELECT EXPIRATION_DATE
 		  FROM PAYMENT
	     WHERE EMAIL = #{email}

SELECT EMAIL, ARTIST
  FROM USERS
 WHERE EMAIL = #{email}
