mapper.select:
SELECT USER_NO, EMAIL, ARTIST, NAME, PW, PROFILE_IMAGE, MOBILE, GENDER, BIRTHYEAR, BIRTHDAY, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, EXTRA_ADDRESS, SNS_TYPE, SESSION_ID, SESSION_LIMIT_DATE, JOIN_DATE, PW_MODIFY_DATE, INFO_MODIFY_DATE, AGREE_CODE 
  FROM USERS


	 	AND EMAIL = #{email}


	 	AND PW = #{pw}


	 	AND SESSION_ID = #{sessionId}
	 	AND SESSION_LIMIT_DATE > NOW()


		 AND MOBILE = #{mobile}


	 	AND NAME = #{name}

mapper.update:
UPDATE ACCESS_LOG
   SET LOGIN_DATE = NOW()
 WHERE EMAIL = #{email}

UPDATE USERS
   SET SESSION_ID = #{sessionId}
   	 , SESSION_LIMIT_DATE = #{sessionLimitDate}
 WHERE EMAIL = #{email}

UPDATE USERS
   SET PW = #{pw}
     , PW_MODIFY_DATE = NOW()
 WHERE USER_NO = #{userNo}

mapper.select:
SELECT PROFILE_IMAGE
  FROM USERS
 WHERE EMAIL = #{email}

mapper.update:
UPDATE USERS
   SET PROFILE_IMAGE = CONCAT(#{profileImagePath},\),#{profileImageFilesystem})
 WHERE EMAIL = #{email}

UPDATE USERS
ARTIST = #{artist}, NAME = #{name}, PW = #{pw}, PW_MODIFY_DATE = NOW(), BIRTHYEAR = #{birthyear}, BIRTHDAY = #{birthday}, MOBILE = #{mobile}, POSTCODE = #{postcode}, ROAD_ADDRESS = #{roadAddress}, JIBUN_ADDRESS = #{jibunAddress}, DETAIL_ADDRESS = #{detailAddress}, EXTRA_ADDRESS = #{extraAddress}, AGREE_CODE = #{agreeCode}, INFO_MODIFY_DATE = NOW()
WHERE EMAIL = #{email}

mapper.select:
SELECT EMAIL, ARTIST, JOIN_DATE, SLEEP_DATE
  FROM SLEEP_USERS
 WHERE EMAIL = #{email}
