mapper.select:
SELECT USER_NO, EMAIL, ARTIST, NAME, PW, PROFILE_IMAGE, MOBILE, GENDER, BIRTHYEAR, BIRTHDAY, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, EXTRA_ADDRESS, SNS_TYPE, SESSION_ID, SESSION_LIMIT_DATE, JOIN_DATE, PW_MODIFY_DATE, INFO_MODIFY_DATE, AGREE_CODE 
  FROM USERS


	 	AND EMAIL = #{email}


	 	AND PW = #{pw}


	 	AND SESSION_ID = #{sessionId}
	 	AND SESSION_LIMIT_DATE > SYSDATE


	 	AND NAME = #{name}

mapper.update:
UPDATE USERS
   SET SESSION_ID = #{sessionId}
   	 , SESSION_LIMIT_DATE = #{sessionLimitDate}
 WHERE EMAIL = #{email}

UPDATE USERS
ARTIST = #{artist}, NAME = #{name} BIRTHYEAR = #{birthyear}, BIRTHDAY = #{birthday}, MOBILE = #{mobile}, POSTCODE = #{postcode}, ROAD_ADDRESS = #{roadAddress}, JIBUN_ADDRESS = #{jibunAddress}, DETAIL_ADDRESS = #{detailAddress}, EXTRA_ADDRESS = #{extraAddress}, AGREE_CODE = #{agreeCode}, INFO_MODIFY_DATE = SYSDATE
WHERE EMAIL = #{email}
