mapper.select:
SELECT USER_NO, EMAIL, PW, ,ARTIST, NAME, GENDER, PROFILE_IMAGE, MOBILE, BIRTHYEAR, BIRTHDAY, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, EXTRA_ADDRESS, SNS_TYPE, JOIN_DATE, PW_MODIFY_DATE, INFO_MODIFY_DATE, SESSION_ID, SESSION_LIMIT_DATE, AGREE_CODE
  FROM USERS


	 	EMAIL = #{email}


	 	AND PW = #{pw}


	 	AND EMAIL = #{email}


	 	AND SESSION_ID = #{sessionId}
	 	AND SESSION_LIMIT_DATE > SYSDATE


	 	AND ARTIST = #{artist}


	 	AND NAME = #{name}

mapper.update:
UPDATE USERS
   SET SESSION_ID = #{sessionId}
   	 , SESSION_LIMIT_DATE = #{sessionLimitDate}
 WHERE EMAIL = #{email}
